mapper.select:
select s.source_id, s.full_name source_name, s.town_name, s.emission_permit_code, s.social_credit_code, s.env_contact_name, s.env_contact_phone, s.industry_name, dd.dict_label source_status_name, s.address, s.longitude, s.latitude, w.warn_level from pf_bas_source s inner join ( select c.pf_source_id, nvl(min(w.warn_level), 0) warn_level from pf_bas_constructioninfo c inner join pf_bas_tag_detail td on td.pf_source_id = c.pf_source_id and td.is_deleted = 0 inner join pf_bas_tag_head th on th.tag_head_id = td.tag_head_id and th.is_deleted = 0 left join dat_warnstat w on w.tag_head_id = c.tag_head_id and w.source_id = c.pf_source_id and w.proc_state = 0 where c.is_deleted = 0 and th.is_active = 1
and w.warn_level = #{warnLevel}
and c.construction_status in
#{status}
and td.tag_head_id in
#{tagHeadId}
group by c.pf_source_id ) w on w.pf_source_id = s.source_id left join sys_dict_data dd on dd.dict_type = 'source_status' and dd.dict_value = s.source_status where s.is_deleted = 0
and s.full_name like '%' || #{sourceName} || '%'
and s.town_code = #{townCode}
${dataScope} order by s.full_name

select dd.dict_label name, count(distinct s.source_id) total from pf_bas_source s inner join pf_bas_tag_detail td on td.pf_source_id = s.source_id and td.is_deleted = 0 inner join pf_bas_tag_head th on th.tag_head_id = td.tag_head_id and th.is_deleted = 0 left join sys_dict_data dd on dd.dict_type = 'source_status' and dd.dict_value = s.source_status where s.is_deleted = 0 and th.is_active = 1
and td.tag_head_id in
#{tagHeadId}
and s.town_code = #{townCode}
${dataScope} group by s.source_status, dd.dict_label order by s.source_status

select w.warn_level, w.warn_desc name, sum(warn_cnt) total
from dat_warnstat w
inner join pf_bas_source s on s.source_id = w.source_id and s.is_deleted = 0
where w.proc_state = 0

    and w.tag_head_id in

        #{tagHeadId}



    and s.town_code = #{townCode}


    and w.warn_date >= #{startDate}


    and w.warn_date < #{endDate} + 1

${dataScope}
group by w.warn_level, w.warn_desc
order by w.warn_level, sum(warn_cnt)
