select td.tag_head_id, s.source_id, th.tag_head_name, s.full_name, s.town_name, s.env_contact_name, s.env_contact_phone, ssd.dict_label source_status, cd.dict_label status, su.login_flag,c.construction_status, c.verify_status, c.short_name, c.construction_person, c.construction_phone, c.audit_date, c.accept_date, ta.task_adjust_id, c.date_updated, c.updated_by from pf_bas_tag_detail td left join pf_bas_tag_head th on th.tag_head_id = td.tag_head_id and th.is_deleted=0 left join pf_bas_source s on s.source_id = td.pf_source_id and s.is_deleted=0 left join pf_bas_constructioninfo c on c.pf_source_id = s.source_id and c.tag_head_id = td.tag_head_id and c.is_deleted=0 left join sys_dict_data ssd on ssd.dict_type = 'source_status' and ssd.dict_value = s.source_status left join sys_dict_data cd on cd.dict_type = 'ty_construct_state' and cd.dict_value = c.construction_status left join pf_bas_task_adjust ta on ta.pf_source_id = s.source_id and ta.tag_head_id = td.tag_head_id and ta.is_deleted = 0 and ta.aduit_status = 0 left join pf_bas_source_user su on su.source_id = s.source_id
and td.tag_head_id IN
#{tagHeadId}
and td.is_deleted=0 and td.list_type IN
#{listType}
and town_code = #{townCode}
and s.source_status = #{sourceStatus}
and c.construction_status = #{constructionStatus}
and su.login_flag = #{loginFlag}
and c.verify_status = #{verifyStatus}
and full_name like '%'||#{fullName}||'%'
and exists (select * from pf_bas_task_adjust ta where ta.tag_head_id = td.tag_head_id and ta.pf_source_id = s.source_id)
and c.verify_status != 1 and c.construction_status in (0,1)
and ((c.construction_status in (2,3,4,6) and c.verify_status != 1) or c.verify_status = 1)
