mapper.select:
select count(case when construction_status = 0 then 1 end) noconnected, count(case when construction_status = 1 then 1 end) constructed, count(case when construction_status between 2 and 4 then 1 end) completed, count(case when construction_status = 6 then 1 end) accepted, count(1) count from pf_bas_constructioninfo c inner join pf_bas_source s on s.source_id = c.pf_source_id inner join pf_bas_tag_detail d on d.pf_source_id = s.source_id and d.tag_head_id = c.tag_head_id inner join pf_bas_tag_head t on t.tag_head_id = d.tag_head_id and t.is_active = 1 where c.is_deleted=0 and s.is_deleted=0 and d.is_deleted = 0 and d.list_type = 0 and c.construction_status >= 0 and s.town_code = #{townCode}
and c.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
${dataScope}

select s.*, sum(decode(defer, null, 0, defer) + decode(apply_not_install, null, 0, apply_not_install) + decode(apply_defer, null, 0, apply_defer) + decode(construction, null, 0, construction) + decode(logged_in, null, 0, logged_in) + decode(not_logged_in, null, 0, not_logged_in)) total from ( select defer, apply_not_install, apply_defer, construction - suspend_construct - apply_ni_construct - apply_ss_construct construction, logged_in - apply_ni_logged_in - apply_ss_logged_in - suspend_logged_in logged_in, not_logged_in - apply_ni_not_logged_in - apply_ss_not_logged_in - suspend_not_logged_in not_logged_in from ( select count(case when construction_status = 1 then 1 end) construction, count(case when construction_status = 1 and verify_status = 2 then 1 end) suspend_construct, count(case when construction_status = 1 and adjust_item = 0 then 1 end) apply_ni_construct, count(case when construction_status = 1 and adjust_item = 1 then 1 end) apply_ss_construct,
count(case when construction_status = 0 and login_flag = 1 then 1 end) logged_in, count(case when (construction_status = 0 and login_flag = 1 and adjust_item = 0) then 1 end) apply_ni_logged_in, count(case when (construction_status = 0 and login_flag = 1 and adjust_item = 1) then 1 end) apply_ss_logged_in, count(case when (construction_status = 0 and login_flag = 1 and verify_status = 2) then 1 end) suspend_logged_in,
count(case when construction_status = 0 and login_flag = 0 then 1 end) not_logged_in, count(case when (construction_status = 0 and login_flag = 0 and adjust_item = 0) then 1 end) apply_ni_not_logged_in, count(case when (construction_status = 0 and login_flag = 0 and adjust_item = 1) then 1 end) apply_ss_not_logged_in, count(case when (construction_status = 0 and login_flag = 0 and verify_status = 2) then 1 end) suspend_not_logged_in,
count(case when verify_status = 2 then 1 end) defer, count(case when adjust_item = 0 then 1 end) apply_not_install, count(case when adjust_item = 1 then 1 end) apply_defer from ( select d.tag_head_id, u.source_id, nvl(u.login_flag,0) login_flag, c.construction_status, c.verify_status, a.adjust_item from pf_bas_tag_detail d inner join pf_bas_constructioninfo c on c.tag_head_id = d.tag_head_id and c.pf_source_id = d.pf_source_id and c.is_deleted = 0 inner join pf_bas_source s on s.source_id = d.pf_source_id and s.is_deleted = 0 left join pf_bas_source_user u on u.source_id = d.pf_source_id left join pf_bas_task_adjust a on a.tag_head_id = c.tag_head_id and a.pf_source_id = c.pf_source_id and a.aduit_status = 0 and a.is_deleted = 0 where d.is_deleted = 0 and d.list_type = 0 and verify_status != 1 and d.is_deleted = 0 and construction_status<= 1 ${dataScope}
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.tag_head_id in (1004, 1006, 1002, 1003, 1012, 1013, 1014, 1015, 1016, 1019, 1017)
and s.town_code = #{townCode}
) ) ) s GROUP BY defer, apply_not_install, apply_defer, construction, logged_in, not_logged_in

select s.*, sum(decode(not_install, null, 0, not_install) + decode(connected, null, 0, connected) + decode(networked, null, 0, networked) + decode(accepting, null, 0, accepting) + decode(accepted, null, 0, accepted)) total from ( select count(case when construction_status <= 1 and verify_status = 1 then 1 end) not_install, count(case when construction_status = 2 then 1 end) connected, count(case when construction_status = 3 then 1 end) networked, count(case when construction_status = 4 then 1 end) accepting, count(case when construction_status = 6 then 1 end) accepted from pf_bas_constructioninfo c inner join pf_bas_tag_detail d on d.tag_head_id = c.tag_head_id and d.pf_source_id = c.pf_source_id and d.is_deleted = 0 inner join pf_bas_source s on s.source_id = c.pf_source_id and s.is_deleted = 0 where c.is_deleted = 0 and d.list_type = 0 ${dataScope}
and c.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and c.tag_head_id in (1004, 1006, 1002, 1003, 1012, 1013, 1014, 1015, 1016, 1019, 1017)
and s.town_code = #{townCode}
) s GROUP BY not_install, connected, networked, accepting, accepted

select h.tag_head_id, count(case when construction_status is not null then 1 end) total, count(case when construction_status in (0, 1) and verify_status != 1 then 1 end) not_finish, count(case when (construction_status in (2, 3, 4, 6) and verify_status != 1) or verify_status = 1 then 1 end) finish from pf_bas_tag_head h left join ( select d.tag_head_id, c.construction_status, c.verify_status from pf_bas_tag_detail d inner join pf_bas_source s on s.source_id = d.pf_source_id and s.is_deleted = 0 inner join pf_bas_constructioninfo c on c.tag_head_id = d.tag_head_id and c.pf_source_id = d.pf_source_id and c.is_deleted = 0 left join pf_bas_task_adjust a on a.tag_head_id = c.tag_head_id and a.pf_source_id = c.pf_source_id and a.aduit_status = 0 and a.is_deleted = 0 left join pf_bas_source_user u on u.source_id = d.pf_source_id where d.list_type = 0 and d.is_deleted = 0
and s.town_code = #{townCode}
${dataScope} ) s on s.tag_head_id = h.tag_head_id where h.tag_head_id in (1002, 1003, 1004, 1006, 1012, 1013, 1014, 1015, 1016, 1017, 1019) group by h.tag_head_id

select count(1) total, count(case when construction_status in (0, 1) and verify_status != 1 then 1 end) not_finish,
count(case when (construction_status in (2, 3, 4, 6) and verify_status != 1) or verify_status = 1 then 1 end) finish
from (
    select c.construction_status, c.verify_status
    from pf_bas_tag_detail d
    inner join pf_bas_source s on s.source_id = d.pf_source_id and s.is_deleted = 0
    inner join pf_bas_constructioninfo c on c.tag_head_id = d.tag_head_id and c.pf_source_id = d.pf_source_id and c.is_deleted = 0
    where d.list_type = 0 and d.is_deleted = 0
    and d.tag_head_id in (1002, 1003, 1004, 1006, 1012, 1013, 1014, 1015, 1016, 1017, 1019)


            and d.tag_head_id in

                #{tagHeadId,jdbcType=BIGINT}




        and s.town_code = #{townCode}

    ${dataScope}
) s

select distinct d.tag_head_id tagHeadId, u.source_id sourceId, s.full_name fullName, s.town_name townName, h.tag_head_name tagHeadName from pf_bas_tag_detail d inner join pf_bas_tag_head h on d.tag_head_id=h.tag_head_id inner join pf_bas_constructioninfo c on c.tag_head_id = d.tag_head_id and c.pf_source_id = d.pf_source_id and c.is_deleted = 0 inner join pf_bas_source s on s.source_id = d.pf_source_id and s.is_deleted = 0 left join pf_bas_source_user u on u.source_id = d.pf_source_id left join pf_bas_task_adjust a on a.tag_head_id = c.tag_head_id and a.pf_source_id = c.pf_source_id and a.aduit_status = 0 and a.is_deleted = 0 where d.is_deleted = 0 and d.tag_head_id in (1002, 1003, 1004, 1006, 1012, 1013, 1014, 1015, 1016, 1017, 1019)
and d.list_type = 0 and verify_status != 1 and construction_status <= 1
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and (c.construction_status > 1 or (construction_status <= 1 and c.verify_status = 1))
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and construction_status <= 1 and c.verify_status = 1
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status = 2
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status = 3
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status = 4
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status = 6
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status = 0 and (u.login_flag = 0 or u.login_flag is null) and c.verify_status = 0 and a.adjust_item is null
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status = 0 and u.login_flag = 1 and c.verify_status = 0 and a.adjust_item is null
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status = 1 and c.verify_status = 0 and a.adjust_item is null
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status <= 1 and c.verify_status = 2
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status <= 1 and a.adjust_item = 1
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0 and c.construction_status <= 1 and a.adjust_item = 0
and d.tag_head_id in
#{tagHeadId,jdbcType=BIGINT}
and d.list_type = 0
and d.list_type = 0 and ((c.construction_status in (2, 3, 4, 6) and c.verify_status != 1) or c.verify_status = 1)
and d.list_type = 0 and not ((c.construction_status in (2, 3, 4, 6) and c.verify_status != 1) or c.verify_status = 1)
and d.list_type = 1 and c.construction_status in (2, 3, 4, 6) and c.verify_status != 1
and s.town_code = #{townCode} and d.tag_head_id = #{tagHeadId}
