mapper.select:
select * from ( select distinct ce.cp_elem_id dataIndex,ce.element_desc || '(' || e.unit || ')' title, o.outlet_name groupName, e.orderby from bas_checkpoint c inner join bas_checkpoint_element ce on ce.collect_id = c.collect_id and ce.is_deleted = 0 inner join bas_outlet o on o.outlet_id = ce.outlet_id and o.is_deleted = 0 inner join bas_element e on ce.ele_type_id = e.ele_type_id and e.is_deleted = 0 where c.source_id = #{sourceId} and c.is_deleted = 0
and o.outlet_id = #{outletId}
) order by orderby,dataIndex

select s.source_id, s.full_name checkpoint_name, dc.construction_unit short_name, r.datadate, ROUND(avg(realcnt/28.8),2) real,ROUND(avg(minucnt/1.44),2) minute, ROUND(avg(hourcnt/0.24), 2) hour, ROUND(avg(daycnt/0.01), 2) day from dat_completerate r inner join bas_checkpoint_element ce on ce.cp_elem_id = r.cp_elem_id and ce.is_deleted = 0 inner join bas_checkpoint c on c.collect_id=ce.collect_id and c.is_deleted = 0 inner join bas_source s on s.source_id = c.source_id inner join dat_constructioninfo dc on dc.source_id = s.source_id and dc.is_deleted = 0 where datadate >= to_date(#{startDate}, 'yyyy-mm-dd') and datadate < to_date(#{endDate}, 'yyyy-mm-dd') +1
and c.source_id = #{sourceId}
and (dc.construction_unit like '%'|| #{shortName}||'%' or dc.SHORT_NAME like '%'|| #{shortName}||'%')
and c.acquisition_brand like '%'|| #{brandName}||'%'
${labelQuery}
${dataScope} group by s.source_id, s.full_name, dc.construction_unit, r.datadate order by datadate desc

select c.source_id,b.element_desc checkpoint_name,c.acquisition_brand brand_name,datadate, ROUND(realcnt/28.8,2) real,ROUND(minucnt/1.44,2) minute, ROUND(hourcnt/0.24, 2) hour, ROUND(daycnt/0.01, 2) day from bas_checkpoint_element b inner join bas_checkpoint c on c.collect_id=b.collect_id and c.is_deleted=0 inner join basecenter.pf_bas_source s on c.source_id=s.source_id and s.is_deleted=0 inner join dat_constructioninfo e on s.source_id = e.source_id and e.is_deleted = 0 left join dat_completerate a on a.cp_elem_id=b.cp_elem_id and datadate = to_date(#{startDate}, 'yyyy-mm-dd') where b.is_deleted=0
and c.source_id = #{sourceId}
and (e.construction_unit like '%'|| #{shortName}||'%' or e.SHORT_NAME like '%'|| #{shortName}||'%')
and c.acquisition_brand like '%'|| #{brandName}||'%'
${labelQuery}
${dataScope} order by b.element_desc desc

select c.source_id,s.full_name checkpoint_name,TRUNC(a.datadate, 'mm') datadate, ROUND(avg(realcnt/28.8),2) real,ROUND(avg(minucnt/1.44),2) minute, ROUND(avg(hourcnt/0.24), 2) hour, ROUND(avg(daycnt/0.01), 2) day, e.construction_unit short_name from dat_completerate a inner join bas_checkpoint_element b on a.cp_elem_id=b.cp_elem_id and b.is_deleted = 0 inner join bas_checkpoint c on c.collect_id=b.collect_id and c.is_deleted = 0 inner join basecenter.pf_bas_source s on c.source_id=s.source_id and s.is_deleted = 0 inner join basecenter.pf_bas_tag_detail d on d.pf_source_id = s.source_id and d.is_deleted = 0 and tag_head_id = '1004' inner join dat_constructioninfo e on d.pf_source_id = e.source_id and e.is_deleted = 0 where TRUNC(a.datadate, 'mm') >= to_date(#{startDate}, 'yyyy-mm') and TRUNC(a.datadate, 'mm') <= to_date(#{endDate}, 'yyyy-mm') + 1
and c.source_id = #{sourceId}
and (e.construction_unit like '%'|| #{shortName}||'%' or e.SHORT_NAME like '%'|| #{shortName}||'%')
and c.acquisition_brand like '%'|| #{brandName}||'%'
${labelQuery}
${dataScope} group by c.source_id,s.full_name,TRUNC(a.datadate, 'mm'),e.construction_unit order by datadate desc

select c.source_id,b.element_desc checkpoint_name,c.acquisition_brand brand_name, TRUNC(a.datadate, 'mm') datadate, ROUND(avg(realcnt/28.8),2) real,ROUND(avg(minucnt/1.44),2) minute, ROUND(avg(hourcnt/0.24), 2) hour,ROUND(avg(daycnt/0.01), 2) day from dat_completerate a inner join bas_checkpoint_element b on a.cp_elem_id=b.cp_elem_id and b.is_deleted=0 inner join bas_checkpoint c on c.collect_id=b.collect_id and c.is_deleted=0 inner join basecenter.pf_bas_source s on c.source_id=s.source_id and s.is_deleted=0 inner join basecenter.pf_bas_tag_detail d on d.pf_source_id = s.source_id and tag_head_id = '1004' inner join dat_constructioninfo e on d.pf_source_id = e.source_id and e.is_deleted = 0 where TRUNC(a.datadate, 'mm') = to_date(#{startDate}, 'yyyy-mm')
and c.source_id = #{sourceId}
and (e.construction_unit like '%'|| #{shortName}||'%' or e.SHORT_NAME like '%'|| #{shortName}||'%')
and c.acquisition_brand like '%'|| #{brandName}||'%'
${labelQuery}
${dataScope} group by c.source_id,b.element_desc,TRUNC(a.datadate, 'mm'),c.acquisition_brand order by datadate desc

select count(1) from dat_warnmsgheads
where proc_State < 2 and source_id = #{sourceId,jdbcType=VARCHAR}
and IS_DELETED = 0
