mapper.select:
select s.town_code, s.town_name,h.warn_level,sd.dict_label, count(1) warn_count, sum(decode(proc_state,0,1,0)) factory_count, sum(decode(proc_state,2,1,0)) duty_count, sum(decode(proc_state,5,1,0)) substation_count, sum(decode(proc_state,6,1,0)) finish_count from dat_warnmsgheads h left join basecenter.sys_dict_data sd on h.warn_level=sd.dict_value and sd.dict_type='ty_warn_level' inner join bas_source s on s.source_id = h.source_id inner join basecenter.pf_bas_constructioninfo c on c.pf_source_id = s.source_id and c.tag_head_id = 1003 and c.is_deleted = 0 where h.is_deleted = 0
and s.town_code = #{townCode}
and h.warn_level = #{warnLevel}
and h.end_date >= to_date(#{beginTime},'yyyy-MM-dd') and h.start_date < to_date(#{endTime},'yyyy-MM-dd')+1
group by s.town_code, s.town_name,h.warn_level,sd.dict_label order by town_code desc

select s.full_name fullName, s.town_name townName,s.town_code townCode, s.social_credit_code socialCreditCode, d.construction_status status, d.short_name shortName,mcnt, hcnt, dcnt from bas_source s inner join dat_constructioninfo d on s.source_id=d.source_id and d.is_deleted=0 left join ( select source_id,round(avg(minucnt / 1.44), 2) mcnt,round(avg(hourcnt / 0.24), 2) hcnt,round(avg(daycnt / 0.01), 2) dcnt from dat_completerate a inner join bas_checkpoint_element b on a.cp_elem_id=b.cp_elem_id and b.is_deleted=0 inner join bas_checkpoint c on c.collect_id=b.collect_id and c.is_deleted=0 where datadate>=trunc(sysdate-7) and datadate < trunc(sysdate) group by source_id ) c on s.source_id=c.source_id where s.is_deleted=0 and construction_status>=3 and not (nvl(mcnt,0)>90 and nvl(hcnt,0)>90 and nvl(dcnt,0)>90)
and s.full_name like '%'|| #{fullName}||'%'
and s.town_name like '%'|| #{townName}||'%'
and s.town_code = #{townCode}
d.short_name like '%'|| #{shortName}||'%'
d.construction_status = #{status}

select s.full_name fullName, s.town_name townName, s.social_credit_code socialCreditCode, trunc(jh.job_date, 'dd') jobDate, avg(d.before_amt) beforeAmt, avg(d.after_amt) afterAmt, avg(d.before_amt) - avg(d.after_amt) amt, sum(jd.factinamt) factInAmt from job_transjobdetail jd inner join job_transjobhead jh on jh.job_headid = jd.job_headid and jh.is_deleted = 0 inner join bas_source s on s.source_id = jd.source_id inner join ( select c.source_id, max(amount) before_amt, min(amount) after_amt from dat_second d inner join bas_checkpoint_element ce on ce.cp_elem_id = d.cp_elem_id and ce.is_deleted = 0 inner join bas_checkpoint c on c.collect_id = ce.collect_id and c.is_deleted = 0 where ce.ele_type_id = 'we00' and trunc(datadate, 'dd') = to_date(#{jobDate},'yyyy-mm-dd') group by c.source_id ) d on d.source_id = jd.source_id where jd.is_deleted = 0 ${dataScope}
and s.full_name like '%' || #{fullName} || '%'
and s.town_code = #{townCode}
and trunc(jh.job_date, 'dd') = to_date(#{jobDate},'yyyy-mm-dd') group by s.full_name, s.town_name, s.social_credit_code, trunc(jh.job_date, 'dd')

select to_char(datadate, 'yyyy-mm-dd') datadate,
case when amt < 0 then 0 else amt end amt
from (
select t.datadate,
d.amt - lag(d.amt, 1, null) over ( order by t.datadate) amt
from (
select trunc(to_date(#{datadate},'yyyy-mm'), 'dd') + level - 2 datadate from dual
connect by level <= to_char(last_day(to_date(#{datadate},'yyyy-mm')),'dd') + 1
) t
left join (
select d.datadate, case when e.val_type < 10 then avg_amt else cou_amt end amt
from dat_day d
inner join bas_checkpoint_element ce on ce.cp_elem_id = d.cp_elem_id
inner join bas_element e on e.ele_type_id = ce.ele_type_id
where ce.cp_elem_id = #{cpElemId}
) d on d.datadate = t.datadate
where t.datadate < sysdate - 1
order by t.datadate
) t
where datadate >= to_date(#{datadate},'yyyy-mm')
