mapper.select:
select s.collect_id, s.full_name FROM bas_checkpoint s where s.is_deleted = 0
and s.source_id = #{sourceId}
order by s.full_name

select c.collect_id, c.ip_address, c.is_online, c.mn, c.source_id, s.full_name sourceName, c.check_date, c.full_name cpName, c.acquisition_brand, c.is_envcert
from bas_checkpoint c
left join bas_source s on s.source_id = c.source_id
where c.is_deleted = 0

    and c.full_name like '%'|| #{cpName}||'%'


    and s.full_name like '%'|| #{sourceName}||'%'


    and s.source_id = #{sourceId}


    and c.mn like '%'|| #{mn}||'%'


    and s.is_online = #{isOnline}

${dataScope}
